<template>
  <div id="loadMore" class="loadmore" @touchstart="touchstart($event)" @touchmove="touchmove($event)"
       @touchend="touchend($event)">
    <div class="pull-wrap">
      <div v-show="openRefresh">
        <transition name="fade">
          <div>
            <i v-if="state==3&&refreshTips" key="1" class="loadmore-icon"></i>
            <i v-else-if="refreshTips" key="2"
               :class="state==1 ? 'pull-arrow pull-toggle': state==2?'pull-arrow':''"></i>
            <span v-if="refreshTips" key="3" class="pull-text">{{ refreshTips }}</span>
          </div>
        </transition>
      </div>
    </div>
    <slot></slot>
    <div class="loadmore-tip" id="loadTips" v-if="totalCount > 0 && totalCount >= pageSize">
      <i v-show="loadTips=='Actualizando'" class="loadmore-icon"></i>
      {{ loadTips }}
    </div>
  </div>
</template>
<script>
var clr, clr2, clr3;
export default {
  props: {
    pageIndex: {
      type: [Number, String],
      default: "1"
    },
    pageSize: {
      type: [Number, String],
      default: "10"
    },
    totalCount: {
      type: [Number, String],
      default: "0"
    },
    tips: {
      type: [String],
      default: "No hay datos disponibles"
    },
    openRefresh: {
      type: [Boolean],
      default: false
    }
  },
  data() {
    return {
      isPull: false,//是否达到了松开刷新的临界点
      state: 1,//1:下拉刷新，2：松开刷新，3：更新中
      refreshTips: '',
      loadTips: "Actualizando"
    };
  },
  watch: {
    pageIndex() {
      this.loadMore();
    },
    totalCount: {
      handler(val, oldval) {
        if (val > 0) {
          this.loadMore();
        }
      },
      immediate: true
    }
  },
  methods: {
    //上滑动加载更多
    loadMore() {
      var scope = this;
      scope.loadTips = "Actualizando";
      let flag = true;
      let pageSize = this.pageSize;
      let pageIndex = this.pageIndex;
      let totalCount = this.totalCount;
      // console.log(pageSize,pageIndex,totalCount)
      return new Promise(function (resolve, reject) {
        window.onscroll = function () {
          clr = setTimeout(() => {
            let scrollHeight = document.documentElement.scrollHeight || document.body.scrollHeight;
            let scrollTop = document.documentElement.scrollTop || document.body.scrollTop;
            let clientHeight = document.documentElement.clientHeight || document.body.clientHeight;
            if (!document.getElementById("loadTips")) {
              return;
            }
            let rectBottom = document.getElementById("loadTips").getBoundingClientRect().bottom | 0;
            //if (scrollTop + clientHeight >= scrollHeight - 25) {
            if (rectBottom < clientHeight + 25) {
              if (flag) {
                flag = false;
                var size = totalCount - pageIndex * pageSize;
                if (size > 0) {
                  scope.$emit("loadmore", pageIndex + 1);
                  resolve(true);
                } else {
                  scope.loadTips = "No hay más datos disponibles";
                  resolve(false);
                }
                clearTimeout(clr);
              }
            }
          }, 400);
        };
      });
    },
    //下拉刷新
    refresh() {
      this._startPos = 0
      this._transitionHeight = 0
      this.obj = document.getElementById("loadMore")
      this.objTop = this.obj.offsetTop
    },
    touchstart(event) {

      if (!this.openRefresh) return
      this._startPos = event.targetTouches[0].screenY
      this.obj.style.transition = "transform 0s linear"
    },
    touchmove(event) {
      if (!this.openRefresh) return
      var h = Math.ceil(this.obj.getBoundingClientRect().top)
      this._transitionHeight = ((event.targetTouches[0].screenY - this._startPos) * 0.4) | 0

      if (h >= this.objTop) {
        if (typeof event.cancelable !== 'boolean' || event.cancelable) {
          event.preventDefault();
        }
        this.obj.style.transform = "translateY(" + this._transitionHeight + "px)"
        if (this._transitionHeight > 0 && this._transitionHeight < 100) {
          this.state = 1
          this.isPull = false
          this.refreshTips = "Actualizar"
          if (this._transitionHeight > 50) {
            this.state = 2
            this.isPull = true
            this.refreshTips = "Suelta para actualizar";
          }
        }
      }
    },
    touchend(event) {
      if (!this.openRefresh) return
      this.obj.style.cssText = `transition:transform 0.2s cubic-bezier(0,.2,.5,.7);transform:translateY(0);`
      if (this.isPull) {
        var h = Math.ceil(this.obj.getBoundingClientRect().top + 40)
        this._transitionHeight = event.changedTouches[0].screenY - this._startPos;
        if (h >= this.objTop) {
          this.state = 3;
          this.refreshTips = "Actualizando";
          this.obj.style.transform = "translateY(40px)";
          clr2 = setTimeout(() => {
            this.obj.style.transform = "translateY(0)";
            clearTimeout(clr2);
          }, 1000);
          clr3 = setTimeout(() => {

            clearTimeout(clr2);
            this.refreshTips = ''
            this.isPull = false;
            this.$emit("refresh", true);
          }, 1500);
        }
      } else {
        clr2 = setTimeout(() => {
          this.state = 1;
          clearTimeout(clr2);
        }, 400);
      }
    }
  },
  destroyed() {
    clearTimeout(clr);
    clearTimeout(clr2);
    clearTimeout(clr3);
  },
  mounted() {
    if (this.openRefresh) {
      this.$nextTick(() => {
        this.refresh()
      })
    }
  }
};
</script>
<style scoped>
.loadmore {
  margin-top: -40px;
}

.fade-enter-active, .fade-leave-active {
  -webkit-transition: all 0.2s ease;
  transition: all 0.2s ease;
}

.fade-enter, .fade-leave-to {
  opacity: 0;
}

.loadmore-tip {
  color: #aaa;
  font-size: 12px;
  text-align: center;
  height: 40px;
  line-height: 40px;
}

.pull-wrap {
  height: 40px;
  line-height: 40px;
  text-align: center;
  width: 100%;
}

.no-data-tip {
  text-align: center;
  padding-top: 36%;
}

.no-data-tip p {
  font-size: 16px;
  margin: 10px auto;
  color: #aaa;
  padding: 0;
}

.no-data-tip img {
  display: block;
  margin: 0 auto;
  width: auto;
  max-width: 150px;
}

@keyframes rotate-loading {
  0% {
    -webkit-transform: rotate(0);
    transform: rotate(0);
  }
  100% {
    -webkit-transform: rotate(360deg);
    transform: rotate(360deg);
  }
}

@-webkit-keyframes rotate-loading {
  0% {
    -webkit-transform: rotate(0);
    transform: rotate(0);
  }
  100% {
    -webkit-transform: rotate(360deg);
    transform: rotate(360deg);
  }
}

.loadmore-icon {
  vertical-align: -2px;
  width: 12px;
  height: 12px;
  display: inline-block;
  background: url("data:image/png;base64,") no-repeat;
  background-size: 12px 12px;
  -webkit-animation: rotate-loading 0.5s linear forwards infinite;
  animation: rotate-loading 0.5s linear forwards infinite;
}

.pull-text {
  text-align: center;
  color: #aaa;
  height: 40px;
  font-size: 12px;
  line-height: 40px;
}

.pull-arrow {
  vertical-align: -6px;
  height: 18px;
  width: 14px;
  display: inline-block;
  background: url("data:image/svg+xml;utf8;base64,") no-repeat;
  transition: all 0.2s ease;
}

.pull-toggle {
  vertical-align: -2px;
  -webkit-transform: rotate(180deg);
  transform: rotate(180deg);
}
</style>
